import{_ as d,c as r,a2 as o,o as e}from"./chunks/framework.M7IgKNHy.js";const h=JSON.parse('{"title":"Словарь FPV-шника","description":"","frontmatter":{},"headers":[],"relativePath":"ru/community/glossarium.md","filePath":"ru/community/glossarium.md","lastUpdated":1731957694000}'),a={name:"ru/community/glossarium.md"};function i(s,t,n,l,p,c){return e(),r("div",null,t[0]||(t[0]=[o('<h1 id="словарь-fpv-шника" tabindex="-1">Словарь FPV-шника <a class="header-anchor" href="#словарь-fpv-шника" aria-label="Permalink to &quot;Словарь FPV-шника&quot;">​</a></h1><table tabindex="0"><thead><tr><th>Кракозябра</th><th>Перевод</th></tr></thead><tbody><tr><td>ЛА</td><td>летательный аппарат</td></tr><tr><td>ВМГ</td><td>винто-моторная группа</td></tr><tr><td>2&quot;, 2.5&quot;, 3&quot;, 3.5&quot;, 4&quot;, 5&quot;, 6&quot;, 7&quot; ..</td><td>диаметр пропеллера дрона, так обозначают типоразмеры/классы дронов</td></tr><tr><td>31мм, 35мм, 40мм, 45мм</td><td>тоже диаметр пропеллера, все меньше 2&quot;, такие обозначения используются в основном среди tiny whoop - ов</td></tr><tr><td>tiny whoop, тинивуп, вуп</td><td>крошечный коптер с расстоянием между моторами по диагонали ~65-75мм, пропгардами (&quot;защитой&quot; пропеллера) на раме, питающийся от 1-2S аккумулятора</td></tr><tr><td>синик, синевуп</td><td>от слова синематик. коптер для плавных пролетов с целью снять киношную картинку, на раме с пропгардами/дактами</td></tr><tr><td>зубочистка</td><td>очень легкий коптер</td></tr><tr><td>батка, бата, АКБ, аккум, батарея</td><td>аккумулятор</td></tr><tr><td>4ы, 4s</td><td>количество ячеек (банок) аккумулятора, в данном примере их 4, в каждой ячейке 4.2В максимальное напряжение в случае LiPo, LiOn; 4.35В на ячейку, если LiHV</td></tr><tr><td>LiPo, LiOn/LiIon, липо, лион</td><td>самые распространенные типы литиевых аккумуляторов</td></tr><tr><td>LiHV, лихв, хв батка</td><td>lithium high voltage, аккумулятор с повышенным напряжением, максимально 4.35В на ячейку, в основном используется для тинивупов</td></tr><tr><td>ирл, IRL</td><td>In Real Life, в реальной жизни</td></tr><tr><td>FPV</td><td>first person view</td></tr><tr><td>OSD</td><td>on screen display</td></tr><tr><td>KV</td><td>количество оборотов на вольт</td></tr><tr><td>TVL</td><td>число телевизионных линий</td></tr><tr><td>сетап, билд, сборка</td><td>одним словом коптер</td></tr><tr><td>стек, стак</td><td>башня из регулятора 4 в 1 с полетником, иногда с видеопередатчиком</td></tr><tr><td>канопа</td><td>canopy - верхняя часть рамы, как правило пластиковая, защищающая электронику</td></tr><tr><td>маунт</td><td>крепеж для камеры</td></tr><tr><td>tx</td><td>передатчик, передает сигнал</td></tr><tr><td>rx</td><td>приемник, принимает сигнал</td></tr><tr><td>втх, vtx</td><td>видеопередатчик</td></tr><tr><td>рег, esc</td><td>регулятор</td></tr><tr><td>приемыш</td><td>приемник</td></tr><tr><td>передаст</td><td>передатчик</td></tr><tr><td>пк, мозг</td><td>полетный контроллер, он же полетник</td></tr><tr><td>редиска</td><td>нехороший человек</td></tr><tr><td>камень</td><td>микропроцессор, он же stm</td></tr><tr><td>кондер</td><td>конденсатор</td></tr><tr><td>резюк</td><td>резистор</td></tr><tr><td>проп</td><td>пропеллер</td></tr><tr><td>аппа</td><td>аппаратура радиоуправления</td></tr><tr><td>бут, boot, дфу, дфю</td><td>бутлоадер, bootloader, он же загрузчик; в чатах обычно имеют в виду режим загрузчика, как правило, активируется при подаче питания с зажатой кнопкой на плате</td></tr><tr><td>клипса</td><td>c-clip</td></tr><tr><td>пантилт, pan-tilt</td><td>поворотная система для антенн и т.п.</td></tr><tr><td>диверсити</td><td>два приемника, чтобы выводить изображение с источника, где лучше сигнал</td></tr><tr><td>кубик</td><td>экшн-камера</td></tr><tr><td>серва</td><td>сервомашинка</td></tr><tr><td>пвм, pwm</td><td>ШИМ</td></tr><tr><td>ппм, сбас</td><td>протоколы радиоуправления</td></tr><tr><td>аккст, аксес</td><td>разновидности протоколов frsky</td></tr><tr><td>бинд</td><td>процесс привязки радиоприемника к передатчику-аппаратуре, также применимо к цифровым видеосистемам</td></tr><tr><td>елрс, кросс, трейсер, гост</td><td>системы радиоуправления, подробнее тут: <a href="https://propwashservice.com/intro/tx.html" target="_blank" rel="noreferrer">https://propwashservice.com/intro/tx.html</a></td></tr><tr><td>ксв</td><td>коэффициент стоячей волны — отношение наибольшего значения амплитуды напряжённости электрического или магнитного поля стоячей волны в линии передачи к наименьшему</td></tr><tr><td>бетка</td><td>betaflight, бетафлайт</td></tr><tr><td>ченл мап</td><td>channel map (по кривому &quot;карта каналов&quot;) - поле для указания порядка каналов вашей аппаратуры в betaflight конфигураторе, разделе receiver. составляется аббревиатура из начальных букв названий каналов по списку из раздела mixes в настройках модели аппаратуры</td></tr><tr><td>акро, стаб, горайзн</td><td>полетные стабилизационные режимы</td></tr><tr><td>фс, fail safe, фейлсейф, фэйлсэйф</td><td>действия дрона при потери связи, возможны варианты drop (отключение двигателей и падение), land (посадка, но такое никто не использует), gps rescue (возрат домой по GPS с автопосадкой, начиная с betaflight 4.4, до 4.4 это возврат в зону сигнала и падение, если не перехвачено управление); в inav вместо rescue режим RTH (return to home)</td></tr><tr><td>таргет</td><td>файл описания конфигурации устройства, с которым компилируется прошивка</td></tr><tr><td>пассфру, пассру, пастру, пасстру</td><td>passthrough - режим, в котором передаются последовательные данные на устройство, подключенное к порту UART/SoftSerial полетника</td></tr><tr><td>десинк, срыв синхры (синхронизации)</td><td>ситуация, когда регулятор (ESC) перестает понимать, какую обмотку мотора коммутировать, чтобы продолжить вращение мотора, как следствие мотор перестает вращаться. обычно происходит при резком изменении оборотов мотора. в некоторых случаях, исправляется настройкой ESC;</td></tr><tr><td>тайминги</td><td>сложно объяснить человеку, который не знает, как работает бесколлекторный мотор; угол, на который магнит должен отставать, когда включаешь обмотку</td></tr><tr><td>питч, ролл, яв, тротл</td><td>названия осей, за которые отвечают стики</td></tr><tr><td>пагода, патч, сосиска</td><td>разновидности антенн для FPV</td></tr><tr><td>рилка</td><td>reelsteady go, программый стаб для видео с GoPro</td></tr><tr><td>накед, голая проха</td><td>&quot;раздетая&quot; gopro hero камера, вес уменьшается с 120 до 26г, питание не от батареи, а от бека 5В</td></tr><tr><td>софтмаунт, демпфер</td><td>виброразвязка</td></tr><tr><td>кз, коза</td><td>короткое замыкание</td></tr><tr><td>синий дым</td><td>магия, на которой работает вся электроника, если он выходит, что-то сгорает и не работает</td></tr><tr><td>спинер жижа</td><td>Spinner protection silicone conformal coating, защитное покрытие для электроники, на основе MG CHEMICALS 422B</td></tr><tr><td>т12, тс100</td><td>известные виды паяльников, с жалами т12 и тс соответственно</td></tr><tr><td>редитускай</td><td>китайская фирма readytosky, как правило делает подделки известных брендов со своим логотипом</td></tr><tr><td>колобок</td><td>рама от aerodyne-rc, в малых кругах также кличка автора</td></tr><tr><td>пигтейл</td><td>коаксиальный кабель</td></tr><tr><td>армиться, арм</td><td>режим для запуска двигателей на минимальных оборотах (газ обязательно в самом низу), без него дрон не полетит</td></tr><tr><td>дизармиться, дизарм</td><td>выключить двигатели, какой бы уровень газа ни был</td></tr><tr><td>джи, джедай, цифра</td><td>цифровое fpv от dji</td></tr><tr><td>толстяк</td><td>dji fpv drone</td></tr><tr><td>волки, улитка, аватар</td><td>walksnail (один из производителей цифровой видеосистемы avatar, подразделение caddx fpv)</td></tr></tbody></table>',2)]))}const m=d(a,[["render",i]]);export{h as __pageData,m as default};
