import{_ as e}from"./chunks/chooseRx.1d182f04.js";import{_ as a,c as r,b as l,d as t,o as i}from"./app.91b8b69c.js";const m=JSON.parse('{"title":"Комплекты FPV квадрокоптеров - какие они и что брать?","description":"","frontmatter":{},"headers":[{"level":2,"title":"Что такое KIT, RTF, PNP, BNF..","slug":"что-такое-kit-rtf-pnp-bnf","link":"#что-такое-kit-rtf-pnp-bnf","children":[{"level":3,"title":"RTF — это \\"Ready to fly\\", что означает \\"готов к полету\\". Также иногда используется название \\"Starter kit\\".","slug":"rtf-—-это-ready-to-fly-что-означает-готов-к-полету-также-иногда-используется-название-starter-kit","link":"#rtf-—-это-ready-to-fly-что-означает-готов-к-полету-также-иногда-используется-название-starter-kit","children":[]},{"level":3,"title":"BNF — \\"Bind and Fly\\", что означает \\"забинди и летай\\"","slug":"bnf-—-bind-and-fly-что-означает-забинди-и-летаи","link":"#bnf-—-bind-and-fly-что-означает-забинди-и-летаи","children":[]},{"level":3,"title":"PNP — Plug and Play, что означает \\"подключи и пользуйся\\".","slug":"pnp-—-plug-and-play-что-означает-подключи-и-пользуися","link":"#pnp-—-plug-and-play-что-означает-подключи-и-пользуися","children":[]},{"level":3,"title":"ARF (KIT наборы) - \\"Almost Ready to Fly\\", то есть «собери и летай».","slug":"arf-kit-наборы-almost-ready-to-fly-то-есть-«собери-и-летаи»","link":"#arf-kit-наборы-almost-ready-to-fly-то-есть-«собери-и-летаи»","children":[]}]},{"level":2,"title":"Что лучше взять новичку?","slug":"что-лучше-взять-новичку","link":"#что-лучше-взять-новичку","children":[]},{"level":2,"title":"Какой коптер выбрать?","slug":"какои-коптер-выбрать","link":"#какои-коптер-выбрать","children":[]}],"relativePath":"shop/KIT.md"}'),o={name:"shop/KIT.md"},n=t('<h1 id="комплекты-fpv-квадрокоптеров-какие-они-и-что-брать" tabindex="-1">Комплекты FPV квадрокоптеров - какие они и что брать? <a class="header-anchor" href="#комплекты-fpv-квадрокоптеров-какие-они-и-что-брать" aria-hidden="true">#</a></h1><h2 id="что-такое-kit-rtf-pnp-bnf" tabindex="-1">Что такое KIT, RTF, PNP, BNF.. <a class="header-anchor" href="#что-такое-kit-rtf-pnp-bnf" aria-hidden="true">#</a></h2><p>В названиях товаров часто встречаются RTF, BNF, PNP И ARF (KIT). Эти аббревиатуры уточняют комплектацию набора.</p><h3 id="rtf-—-это-ready-to-fly-что-означает-готов-к-полету-также-иногда-используется-название-starter-kit" tabindex="-1">RTF — это &quot;Ready to fly&quot;, что означает &quot;готов к полету&quot;. Также иногда используется название &quot;Starter kit&quot;. <a class="header-anchor" href="#rtf-—-это-ready-to-fly-что-означает-готов-к-полету-также-иногда-используется-название-starter-kit" aria-hidden="true">#</a></h3><p><strong>В RTF набор квадрокоптера входят:</strong></p><ul><li>Квадрокоптер с приемником, совместимым с пультом;</li><li>Пульт управления;</li><li>Аккумулятор (или несколько);</li><li>Зарядное устройство для комплектного аккумулятора.</li></ul><p>Как правило, такие наборы состоят из самых дешевых частей, их часто дарят или покупают ввиду неопытности, в большистве случаев хоббисты настоятельно <strong>не рекомендуют</strong> начинать с таких, поскольку при желании расширить свой ассортимент дронов, скорее всего, придется покупать все с нуля, а этот набор будет лежать без дела. Со временем на рынке появляются варианты RTF китов получше. Перед покупкой рекомендуется проконсультироваться с хоббистами насчет актуальности выбранного набора.</p><h3 id="bnf-—-bind-and-fly-что-означает-забинди-и-летаи" tabindex="-1">BNF — &quot;Bind and Fly&quot;, что означает &quot;забинди и летай&quot; <a class="header-anchor" href="#bnf-—-bind-and-fly-что-означает-забинди-и-летаи" aria-hidden="true">#</a></h3><blockquote><p>что такое &quot;бинд&quot; смотри <a href="https://propwashservice.ru/community/glossarium.html" target="_blank" rel="noreferrer">тут</a></p></blockquote><p><strong>В BNF набор квадрокоптера входят:</strong></p><ul><li>Квадрокоптер с припаянным приемником;</li><li>(опционально) Аккумулятор и соответствующее зарядное устройство, актуально для tiny whoop-ов.</li></ul><p>Поскольку различные системы радиоуправления <strong>не совместимы</strong> между собой (подробнее про <a href="https://propwashservice.ru/intro/tx.html#%D0%BF%D1%80%D0%BE%D1%82%D0%BE%D0%BA%D0%BE%D0%BB%D1%8B-%D0%BF%D0%B5%D1%80%D0%B5%D0%B4%D0%B0%D1%87%D0%B8-%D0%B4%D0%B0%D0%BD%D0%BD%D1%8B%D1%85" target="_blank" rel="noreferrer">актуальные системы тут</a> ), покупателю часто предлагается выбор приемника, если такого нет, то название приемника указано в названии/описании BNF набора. Также в таких опциях может быть предложен выбор камеры, актуально для дронов на DJI FPV с caddx vista/air unit, и выбор винто-моторной группы (в опциях указывается количество ячеек аккумулятора, S, пример на картинке)</p><p align="center"><img src="'+e+'" width="600" alt="пример вариаций квадрокоптера с приемниками"></p>',13),p=t('<h3 id="pnp-—-plug-and-play-что-означает-подключи-и-пользуися" tabindex="-1">PNP — Plug and Play, что означает &quot;подключи и пользуйся&quot;. <a class="header-anchor" href="#pnp-—-plug-and-play-что-означает-подключи-и-пользуися" aria-hidden="true">#</a></h3><p><strong>В PNP набор квадрокоптера входит:</strong></p><ul><li>Квадрокоптер без приемника.</li></ul><p>Как вы уже догадались, к нему вам понадобится в первую очередь купить приемник, припаять его, далее пройти те же шаги, что для BNF набора.</p><h3 id="arf-kit-наборы-almost-ready-to-fly-то-есть-«собери-и-летаи»" tabindex="-1">ARF (KIT наборы) - &quot;Almost Ready to Fly&quot;, то есть «собери и летай». <a class="header-anchor" href="#arf-kit-наборы-almost-ready-to-fly-то-есть-«собери-и-летаи»" aria-hidden="true">#</a></h3><p>КИТ наборы квадрокоптеров подразумевают комплект всех деталей, необходимых для сборки, но современных наборов очень мало, чаще можно встретить в штатах от каких-то крупных блогеров или брендов, на алиэкспресс и подобных площадках чаще встречаются старые неактуальные варианты, которые брать без рекомендаций хоббистов нежелательно. KIT набор самолета часто подразумевает только тушку самолета без электроники, внимательно изучайте описание товара.</p><h2 id="что-лучше-взять-новичку" tabindex="-1">Что лучше взять новичку? <a class="header-anchor" href="#что-лучше-взять-новичку" aria-hidden="true">#</a></h2><p>Зависит от новичка, бюджета и хотелок. С 2020 года на рынке стали появляться неплохие бюджетные аппараты в уже собранном виде. После их появления стало не всегда выгодно собирать самому. Определенные запросы по типу &quot;лучший гоночный квад&quot; или &quot;самый долго летающий дрон&quot; требуют рассчетов, тестов, и на текущий момент нет готовых решений, нужно самостоятельно собирать. Но под основные хотелки уже собраны аппараты, и ничего плохого в покупке готового нет. Стоит лишь отметить, что важно при выборе такого проконтролировать, что дрон рассчитан на желаемое количество ячеек аккумулятора, имеет приемник нужного протокола либо без него для самостоятельного припаивания, и на борту установлена необходимая видеосистема (цифровая или аналоговая).</p><h2 id="какои-коптер-выбрать" tabindex="-1">Какой коптер выбрать? <a class="header-anchor" href="#какои-коптер-выбрать" aria-hidden="true">#</a></h2><p>Подробнее про <a href="https://propwashservice.ru/intro/flightstyles.html" target="_blank" rel="noreferrer">стили полета тут</a>.</p><p>Резюме:</p><p>Из класса тинивупов: happymodel mobula6/moblite6.</p><p>Из класса синевупов: flywoo cinerace20, geprc cinelog 25/30, betafpv pavo30 для naked gopro. geprc cinelog 30/35 для полной гопро.</p><p>Из класса зубочисток: hglrc pertel 3&quot;, betafpv hx115, geprc smart 2.5/3.5&quot;, happymodel crux 3/3.5&quot;.</p><p>Из класса гоночных: foxeer caesar, iflight mach r5, hglrc wind5, QUADRO.TEAM TYPE-R.</p><p>Из класса фристайловых: iflight nazgul/evoque/chimera5/titan/aos 5, geprc mark 5.</p><p>Из класса долголетов: geprc crocodile baby 4/5/7 pro, hglrc rekon 4/5/7, flywoo explorer, iflight chimera7,</p><p>Из класса синелифтеров: Quadlabs Siccario FPV Cinelifter, iFlight Taurus X8, Nurk FPV Special Edition Merica 9.</p>',18);function d(h,s,c,u,f,g){return i(),r("div",null,[n,l(" После покупки такого набора вам понадобиться обновить прошивку приемника и произвести бинд с аппаратурой, в случае цифровой видеосистемы обновить прошивку на ней и также произвести бинд с очками, в настройках прошивки полетного контроллера (по умолчанию в большинстве готовых дронов betaflight, смотрите внимательно описание товара) выставить правильный мапинг каналов (подробнее https://oscarliang.com/channel-map/) и указать режимы для тумблеров (подробнее https://oscarliang.com/betaflight-modes/). "),p])}const b=a(o,[["render",d]]);export{m as __pageData,b as default};
